package CreateThread;import java.util.concurrent.ExecutorService;import java.util.concurrent.Executors;public class ThreadPoolTest {    public static void main(String[] args) {        ExecutorService executorService = Executors.newSingleThreadExecutor();        executorService.submit(new Runnable() {            @Override            public void run() {                System.out.println("线程池");            }        });        executorService.shutdown();    }}