package Synchronized;import java.util.concurrent.Semaphore;import java.util.concurrent.TimeUnit;public class SemaphoreTest {    public static void main(String[] args) {        Semaphore semaphore = new Semaphore(5);        for (int i = 0; i < 7; i++) {            new Thread(()->{                try {                    semaphore.acquire();                    System.out.println(Thread.currentThread().getName()+"抢到了车位。。。");                    try { TimeUnit.SECONDS.sleep(3); } catch (InterruptedException e) {e.printStackTrace(); }                    System.out.println(Thread.currentThread().getName()+"停车3秒后；离开。。。");                } catch (InterruptedException e) {                    e.printStackTrace();                } finally {                    semaphore.release();                }            },"线程" + i).start();        }    }}