package ThreadLocal;public class ThreadLocalTest {    private String context;    public String getContext() {        return context;    }    public void setContext(String context) {        this.context = context;    }    public static void main(String[] args) {        ThreadLocalTest threadLocalTest = new ThreadLocalTest();        for (int i = 0; i < 5; i++) {            Thread thread = new Thread(new Runnable() {                @Override                public void run() {                    threadLocalTest.setContext(Thread.currentThread().getName()+"的数据");                    System.out.println("--------------------------");                    System.out.println(Thread.currentThread().getName()+"------------>"+threadLocalTest.getContext());                }            });            thread.setName("线程"+i);            thread.start();        }    }}